import { useEffect } from 'react';
import './App.css';

function App() {

  const[items,setItems]= useState([]);
  const[visible,setVisible]= useState(3);
useEffect(()=>{
  fetch("https://www.themealdb.com/api/json/v1/1/filter.php?a=british")
  .then((res)=>res.json)
  .then((data)=> setItems(data.meals))
})

  return (
    <div className="App">
      <h1 className='header'>BritishMeal<img src='https://tr.m.wikipedia.org/wiki/Dosya:Flag_of_the_United_Kingdom_%283-5%29.svg'></img> </h1>
      <div className='container'>
        {items.map((item)=>(
          <div className='card'>
            <div className='image'>
              <img src= {item.strMealThumb}/>
            </div>
            <p>{item.strMeal}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
